button.query:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

CONSTRUCT {
	?property
		rdfs:isDefinedBy ?label ;
		rdfs:label ?label ;
		rdfs:comment ?comment ;
		rdfs:domain ?domain ;
		rdfs:range ?range ;
		rdfs:subPropertyOf ?superProperty .
	?subProperty rdfs:subPropertyOf ?property
}
WHERE {
	?property a rdf:Property .
	OPTIONAL {
		?property rdfs:isDefinedBy ?label
	} .
	OPTIONAL {
		?property rdfs:label ?label
	} .
	OPTIONAL {
		?property rdfs:comment ?comment
	} .
	OPTIONAL {
		?property rdfs:domain ?domain
	} .
	OPTIONAL {
		?property rdfs:range ?range
	} .
	OPTIONAL {
		?property rdfs:subPropertyOf ?superProperty
	} .
	OPTIONAL {
		?property ^rdfs:subPropertyOf ?subProperty
	}
}

# See https://github.com/ukparliament/VocabularyBrowser/blob/master/VocabularyBrowser/Controllers/CollectionController.cs#L51

PREFIX : <urn:>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

CONSTRUCT {
	?collection
		:selected true ;
		a skos:Collection ;
		skos:prefLabel ?collectionLabel ;
		skos:member ?concept, ?child .
	?concept
		a skos:Concept ;
		skos:prefLabel ?conceptLabel .
	?parent
		a skos:Collection ;
		skos:member ?collection ;
		skos:prefLabel ?parentLabel .
	?child
		a skos:Collection ;
		skos:prefLabel ?childLabel
}
WHERE {
	BIND (?parameter AS ?collection) .
	?collection skos:prefLabel ?collectionPrefLabel .
	BIND (STR(?collectionPrefLabel) AS ?collectionLabel) .
	OPTIONAL {
		?concept
			a skos:Concept ;
			^skos:member ?collection ;
			skos:prefLabel ?conceptPrefLabel .
		BIND (STR(?conceptPrefLabel) AS ?conceptLabel)
	} .
	OPTIONAL {
		?parent
			a skos:Collection ;
			skos:member ?collection ;
			skos:prefLabel ?parentPrefLabel .
		BIND (STR(?parentPrefLabel) AS ?parentLabel)
	} .
	OPTIONAL {
		?child
			a skos:Collection ;
			^skos:member ?collection ;
			skos:prefLabel ?childPrefLabel .
		BIND (STR(?childPrefLabel) AS ?childLabel)
	}
}

# See https://www.wikidata.org/wiki/Wikidata:SPARQL_query_service/queries/examples#Number_of_handed_out_academy_awards_per_award_type

BASE <http://www.wikidata.org/>
PREFIX p: <prop/>
PREFIX pq: <prop/qualifier/>
PREFIX ps: <prop/statement/>
PREFIX wdt: <prop/direct/>
PREFIX wd: <entity/>
PREFIX bd: <http://www.bigdata.com/rdf#>
PREFIX wikibase: <http://wikiba.se/ontology#>

SELECT ?awardCount ?award ?awardLabel
WHERE {
	{
		SELECT (COUNT(?award) AS ?awardCount) ?award
		WHERE {
			{
				SELECT (SAMPLE(?human) AS ?human) ?award ?awardWork (SAMPLE(?director) AS ?director) (SAMPLE(?awardEdition) AS ?awardEdition) (SAMPLE(?time) AS ?time)
				WHERE {
					?award wdt:P31 wd:Q19020 .
					{
						?human
							p:P166 ?awardStat ;
							wdt:P31 wd:Q5 .
						?awardStat
							ps:P166 ?award ;
							pq:P805 ?awardEdition ;
							pq:P1686 ?awardWork
					}
					UNION
					{
						?awardWork
							wdt:P31 wd:Q11424 ;
							p:P166 ?awardStat .
						?awardStat
							ps:P166 ?award ;
							pq:P805 ?awardEdition
					} .
					OPTIONAL {
						?awardEdition wdt:P585 ?time .
						?awardWork wdt:P57 ?director
					}
				}
				GROUP BY ?awardWork ?award
			}
		}
		GROUP BY ?award
		ORDER BY ?awardCount
	} .
	SERVICE wikibase:label {
		bd:serviceParam wikibase:language '[AUTO_LANGUAGE],en'
	}
}

# See https://github.com/ukparliament/Query/blob/master/Parliament.Data.Api.FixedQuery/Sparql/work_package_by_id.sparql

PREFIX ex: <https://example.com/>
PREFIX : <http://id.parliament.uk/schema/>

CONSTRUCT {
	?package
		a :WorkPackage ;
		:workPackageHasWorkPackageableThing ?workPackaged ;
		:workPackageHasProcedure ?procedure ;
		:workPackageHasBusinessItem ?item .
	?workPackaged
		a :WorkPackageableThing, ?class ;
		:workPackageableThingName ?workPackagedThingName ;
		:workPackageableThingHasWorkPackageableThingWebLink ?workPackagedLink .
	?procedure
		a :Procedure ;
		:procedureName ?procedureName .
	?item
		a :BusinessItem ;
		:businessItemHasProcedureStep ?step ;
		:businessItemDate ?itemDate ;
		:businessItemHasBusinessItemWebLink ?itemLink .
	?step
		a :ProcedureStep, ex:Actualized ;
		:procedureStepName ?stepName ;
		:procedureStepHasHouse ?house ;
		:procedureStepHasBusinessItem ?item ;
		ex:ledTo ?otherStep ;
		ex:canLeadTo ?otherStep .
	?enablingStep
		ex:enables ?possibleStep ;
		ex:canLeadTo ?possibleStep .
	?possibleStep
		a :ProcedureStep, ex:Possible ;
		:procedureStepName ?possibleName ;
		:procedureStepHasHouse ?possibleHouse .
	?rootStep a ex:Root .
	?house
		a :House ;
		:houseName ?houseName .
	?possibleHouse
		a :House ;
		:houseName ?possibleHouseName
}
WHERE {
	BIND (?work_package_id AS ?package) .
	?package :workPackageHasProcedure ?procedure .
	?procedure :procedureName ?procedureName .
	OPTIONAL {
		?package :workPackageHasWorkPackagedThing ?workPackaged .
		?workPackaged a ?class .
		FILTER ((?class = :StatutoryInstrumentPaper || ?class = :ProposedNegativeStatutoryInstrumentPaper) || ?class = :Treaty) .
		OPTIONAL {
			?workPackaged :workPackagedThingName ?workPackagedThingName
		} .
		OPTIONAL {
			?workPackaged :workPackagedThingHasWorkPackagedThingWebLink ?workPackagedLink
		}
	} .
	OPTIONAL {
		{
			?enablingStep :procedureStepHasBusinessItem/:businessItemHasWorkPackage ?package .
			?possibleRoute
				:procedureRouteHasProcedure ?procedure ;
				:procedureRouteIsFromProcedureStep ?enablingStep ;
				:procedureRouteIsToProcedureStep ?possibleStep .
			?possibleStep :procedureStepName ?possibleName .
			OPTIONAL {
				?possibleStep :procedureStepHasHouse ?possibleHouse .
				?possibleHouse :houseName ?possibleHouseName
			} .
			FILTER NOT EXISTS {
				?possibleStep :procedureStepHasBusinessItem/:businessItemHasWorkPackage ?package
			} .
			FILTER NOT EXISTS {
				?precludingRoute
					:procedureRouteHasProcedure ?procedure ;
					:procedureRouteIsToProcedureStep ?possibleStep ;
					:precludedProcedureRouteIsPrecludedByProcedureStep/:procedureStepHasBusinessItem/:businessItemHasWorkPackage ?package
			} .
			FILTER NOT EXISTS {
				?requiringRoute
					:procedureRouteHasProcedure ?procedure ;
					:requiredProcedureRouteIsRequiredByProcedureStep ?possibleStep .
				FILTER NOT EXISTS {
					?requiringRoute :procedureRouteIsToProcedureStep/:procedureStepHasBusinessItem/:businessItemHasWorkPackage ?package
				}
			}
		}
		UNION
		{
			?item :businessItemHasWorkPackage ?package .
			?package :workPackageHasProcedure ?procedure .
			OPTIONAL {
				?item :businessItemDate ?itemDate
			} .
			OPTIONAL {
				?item :businessItemHasBusinessItemWebLink ?itemLink
			} .
			?step
				:procedureStepHasBusinessItem ?item ;
				:procedureStepName ?stepName .
			OPTIONAL {
				?step :procedureStepHasHouse ?house .
				?house :houseName ?houseName
			} .
			OPTIONAL {
				?actualizedRoute
					:procedureRouteHasProcedure ?procedure ;
					:causedProcedureRouteIsCausedByProcedureStep|:allowedProcedureRouteIsAllowedByProcedureStep ?step ;
					:procedureRouteIsToProcedureStep ?otherStep .
				?otherStep :procedureStepHasBusinessItem/:businessItemHasWorkPackage ?package .
				FILTER (?otherStep != ?step)
			}
		}
		UNION
		{
			?rootStep :procedureStepHasBusinessItem/:businessItemHasWorkPackage ?package .
			OPTIONAL {
				?higherRoute
					:procedureRouteHasProcedure ?procedure ;
					(:causedProcedureRouteIsCausedByProcedureStep|:allowedProcedureRouteIsAllowedByProcedureStep)/:procedureStepHasBusinessItem/:businessItemHasWorkPackage ?package ;
					:procedureRouteIsToProcedureStep ?rootStep
			} .
			FILTER (!BOUND(?higherRoute))
		}
	}
}

# See https://data.europa.eu/euodp/en/Sample_SPARQL_queries#ODP-Developerscorner-SampleSPARQLqueries-Retrievedatasetwithspecifictitle(eg.'RegisterofCommissiondocuments')

PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX odp: <http://data.europa.eu/euodp/ontologies/ec-odp#>
PREFIX dc: <http://purl.org/dc/terms/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>

SELECT ?Graph ?DatasetTitle ?Publisher ?DatasetURI
WHERE {
	GRAPH ?Graph {
		?DatasetURI
			a dcat:Dataset ;
			dc:publisher ?Publisher ;
			dc:title ?DatasetTitle .
		FILTER REGEX(?DatasetTitle, 'Register of Commission documents', 'i')
	}
}
LIMIT 10

# See https://graphdb.ontotext.com/documentation/9.2/free/devhub/rdf-sparql-star.html
PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX ex: <http://example.com/>
SELECT ?p ?a ?c WHERE { BIND (<< ?p foaf:age ?a >> AS ?t) . ?t ex:certainty ?c }

# See http://vos.openlinksw.com/owiki/wiki/VOS/VirtTipsAndTricksSPARQL11Insert#INSERT%20%28Informative%29%20Examples

BASE <http://example.com>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

INSERT DATA {
	GRAPH <urn:sparql:tests:insert:informative> {
		<#book1>
			<http://purl.org/dc/elements/1.1/title> 'Fundamentals of Compiler Design' ;
			<http://purl.org/dc/elements/1.1/date> '1977-01-01T00:00:00-02:00'^^xsd:dateTime .
		<#book2>
			<#price> 42 ;
			<http://purl.org/dc/elements/1.1/title> 'David Copperfield' ;
			<http://purl.org/dc/elements/1.1/creator> 'Edmund Wells' ;
			<http://purl.org/dc/elements/1.1/date> '1948-01-01T00:00:00-02:00'^^xsd:dateTime
	}
} ;

INSERT DATA {
	GRAPH <urn:sparql:tests:insert:informative2> {
		<#book4> <http://purl.org/dc/elements/1.1/title> 'SPARQL 1.0 Tutorial'
	}
} ;

INSERT {
	GRAPH <urn:sparql:tests:insert:informative2> {
		?book ?p ?v
	}
}
WHERE {
	GRAPH <urn:sparql:tests:insert:informative> {
		?book <http://purl.org/dc/elements/1.1/date> ?date .
		FILTER (?date > '1970-01-01T00:00:00-02:00'^^xsd:dateTime) .
		?book ?p ?v
	}
}
